function updateDecoration(newDecoration) {
    value.value = { ...value.value, decoration: newDecoration }
    if (newDecoration === 'custom') {
        pickerOpen.value = true
    }
}

function updateUrl(newUrl) {
    value.value = { ...value.value, url: newUrl }
}

function updatePickerOpen(newPickerOpenValue) {
    pickerOpen.value = newPickerOpenValue
}
</script>

<template>
    <div class="setting decoration-picker-setting" :title="setting.title">
        <Icon class="setting-icon">wallpaper</Icon>
        <div class="swatches-wrapper">
            <button v-for="(decoration, i) in decorations" :key="i" class="swatch" :style="decoration.style"
                :title="decoration.name" @click="updateDecoration(decoration.id)"
                :data-state="value.decoration === decoration.id">
                <Transition name="swatch-check">
                    <div v-if="value.decoration === decoration.id">
                        <div class="state-layer"></div>
                        <Icon v-if="decoration.id === 'custom'">add_photo_alternate</Icon>
                        <Icon v-else>check</Icon>
                    </div>
                    <div v-else-if="decoration.id === 'none'">
                        <Icon>remove_selection</Icon>
                    </div>
                    <div v-else-if="decoration.id === 'custom'">
                        <Icon>add_photo_alternate</Icon>
                    </div>
                </Transition>
            </button>
        </div>

        <ImageUrlSheet :model-value="value.url" @update:model-value="updateUrl" :pickerOpen="pickerOpen"
            @update:pickerOpen="updatePickerOpen" />
    </div>
</template>

<style scoped>
.decoration-picker-setting {
    display: grid;
    grid-template-columns: 26px 1fr;
    align-items: center;
    gap: 6px;
    column-gap: 12px;
    padding-top: 8px;
}

.decoration-picker-setting>.setting-icon {
    font-size: 18px;
    scale: 1.4;
    color: var(--color-on-surface-variant);
    justify-self: center;
}

.swatches-wrapper {
    display: flex;
    height: 40px;
    gap: 6px;
}

.swatch {
    position: relative;
    min-width: 40px;
    border: none;
    border-radius: 28px;
    cursor: pointer;
    outline: 1px solid var(--color-outline);
    overflow: hidden;
    color: #fff;
    background-color: var(--palette-primary);
    transition: border-radius 200ms, flex-grow 200ms, background-color 200ms;
}

.swatch:first-child:nth-last-child(2),
.swatch:last-child:nth-child(2) {
    background-color: transparent;
    color: var(--color-on-surface);
}

.swatch:first-child:nth-last-child(2) .state-layer,
.swatch:last-child:nth-child(2) .state-layer {
    background-color: var(--color-secondary-container);
    opacity: 1;
}

.swatch:hover,
.swatch:focus-visible {
    border-radius: 10px;
}

.swatch .state-layer {
    position: absolute;
    inset: 0;
    background-color: var(--color-scrim);
    opacity: .3;
}

.swatch .icon {
    position: absolute;
    top: 50%;
    left: 50%;
